import React, { Component } from "react";
import httpClient from "../../util/httpClient";
import notify from "../../util/notify";
import ImageGallery from "react-image-gallery";
import "./productDetails.component.css";
import { Icon } from "@iconify/react";
import shoppingCartOutlined from "@iconify/icons-ant-design/shopping-cart-outlined";
import checkOutlined from "@iconify/icons-ant-design/check-outlined";

import "react-image-gallery/styles/css/image-gallery.css";
const imgURL = process.env.REACT_APP_IMG_URL;

export class ProductDetails extends Component {
  constructor() {
    super();
    this.state = {
      isLoading: false,
      product: "",
      galImages: []
    };
  }
  componentDidMount() {
    this.productId = this.props.match.params["id"];
    this.setState({
      isLoading: true,
    });
    httpClient
      .GET(`/product/${this.productId}`, true)
      .then((response) => {
        this.setState({
          product: response.data,
        });
        console.log("response propduct is>>", this.state.product);
      })
      .catch((err) => {
        notify.handleError(err);
      })
      .finally(() => {
        this.setState({
          isLoading: false,
        });
      });
  }
  render() {
    // console.log('discount is>>', this.state.product.discount);
    // const { discount } = this.state.product;

    console.log("product is >>>", this.state.product);

    const { images } = this.state.product;

    console.log('images gallery is>>',images);
    for(let imag in images){
      let imgs = 
      [{
        original: `${imgURL}/${imag}`,
        thumbnail: `${imgURL}/${imag}`,
      }
    ]
    }




    return (
      <div className="container">
        <div className="product-details">
          <div className="row">
            <div className="col-md-6">
              <div>
                  <ImageGallery
                  items={imgs}
                  lazyLoad={true}
                  showThumbnails={false}
                  slideInterval={2000}
                  showPlayButton={false}
                  showFullscreenButton={false}
                  slideOnThumbnailHover={false}
                  sizes={500 * 500}
                />


              </div>
            </div>
            <div className="col-md-6">
              <div className="product-contents">
                <div className="product-name">
                  <h3>{this.state.product.name}</h3>
                </div>
                <div className="product-ratings">
                  {/* product ratings */}
                </div>
                <div className="product-lis">
                  <ul>
                    <li>
                      Status :{" "}
                      <p className="green">{this.state.product.status}</p>
                    </li>
                    <li className="light-line">|</li>
                    <li>
                      Weight : <p>{this.state.product.weight}</p>
                    </li>
                  </ul>
                </div>
                <div className="product-lis">
                  <ul>
                    <li>
                      Brand : <p>{this.state.product.brand}</p>
                    </li>
                    <li className="light-line">|</li>
                    <li>
                      Model : <p>{this.state.product.modelNo}</p>
                    </li>
                    <li className="light-line">|</li>
                    <li>
                      Color : <p>{this.state.product.color}</p>
                    </li>
                  </ul>
                </div>
                <div className="product-price">
                  <h2>NRs. {this.state.product.price}</h2>
                  <div className="discount-div">

                    {/* <p>{(this.state.product.discount && this.state.product.discount.discountType == 'percentage')
                          ? <p>{this.state.product.price} - (({this.state.product.discount && this.state.product.discount.discountValue}*{this.state.product.price})/100)</p>
                          : <p>{this.state.product.price} - {this.state.product.discount && this.state.product.discount.discountValue} </p>
                    }</p> */}
                     <p> disount - NRs.{this.state.product.discount && this.state.product.discount.discountValue}</p>

                  </div>
                </div>
                <div className="details-buttons">
                  <div className="add-to-cart">
                    <button className="btn-cart">
                      <Icon icon={shoppingCartOutlined} /> Add to Cart
                    </button>
                  </div>
                  <div className="buy-now">
                    <button className="btn-buy">
                      <Icon icon={checkOutlined} /> Buy Now
                    </button>
                  </div>
                </div>
                <div className="product-desc">
                  <h5 className="strong">Description :</h5>
                  <p>{this.state.product.description}</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
