          <tbody>
            {this.props.products.length > 0  &&
              this.props.products.map((item, index) => (

                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>  

                  </td>
                  <td>
                    <img
                      src={`${imgURL}/${item.images[0]}`}
                      alt="product_img.jpg"
                      width="150px"
                      height="150px"
                    />
                  </td>
                  <td>{item.name}</td>
                  <td>NRs. {item.price}</td>
                  <td>{item.brand}</td>
                  <td>{item.quantity}</td>

                  <td>
                    <Link to="/cart">
                      <Icon icon={plusOutlined} />
                    </Link>
                    <Link to="/cart">
                      <Icon icon={minusOutlined} />
                    </Link>
                  </td>
                  <td>
                    <button className="btn-icons" title="remove">
                      <Icon icon={closeSquareFilled} color="red" />
                    </button>
                  </td>
                </tr>
              ))}
          </tbody>
        </table>
          }else{
            <p>Nothing</p>
          }
        })

      // // ) : (
      // //   <p>Nothing</p>
      // );

    return (
      <div className="container page-div">
        <h1>You have Ordered :</h1>
        <div className="cart-page">{addedItems}</div>
      </div>
    );
  }
}

const mapStateToProps = (store) => ({
  products: store.product.products,
  basketNumbers : store.product.basketNumbers
});

const mapDispatchToProps = (dispatch) => ({
  fetch: () => dispatch(fetchProduct_ac()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Cart);
