import { SET_IS_LOADING, PRODUCT_RECEIVED } from './type';
import httpClient from './../../components/util/httpClient';
import notify from './../../components/util/notify';

// function fetchProduct_ac(params){
//     return function(dispatch){
//         //calling a dispatch will execute reducer
//     }
// } 

export const fetchProduct_ac = (params) => dispatch => {
    console.log('At Actions from view >>',params);
    dispatch({
        type: SET_IS_LOADING,
        payload: true
    })
      httpClient
        .GET("/product", true)
        .then((response) => {
          response.data.forEach((item) => {
            item.$$isDeleting = false;
          });
          //dispatch action to reducer
          console.log('there is a delay with http call now dispatch actin to reducer');
          dispatch({
              type: PRODUCT_RECEIVED,
              payload: response.data
          })
        })
        .catch((err) => {
          notify.handleError(err);
        })
        .finally(() => {
        //   this.setState({
        //     isLoading: false,
        //   });
        });
}
