import React, { Component } from "react";
import SliderHome from "../common/slider/sliderHome.component";
import { connect } from "react-redux";
import { fetchProduct_ac } from "../../actions/products/productActions";
import { Icon } from "@iconify/react";
import shoppingCartOutlined from "@iconify/icons-ant-design/shopping-cart-outlined";
import {Link} from 'react-router-dom';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const imgURL = process.env.REACT_APP_IMG_URL;

class Landing extends Component {
  constructor() {
    super();
  }
  componentDidMount() {
    // console.log('products in state', this.state);
    // console.log('products in props', this.props);
    this.props.fetch();

  }
  render() {
    var settings = {
      dots: false,
      infinite: true,
      speed: 500,
      slidesToShow: 5,
      slidesToScroll: 1
    };

    return (
      <div className="">
        <SliderHome />
        <div className="container">
          {/* Featured Products */}
          <div className="featured-products">
            <h2>Featured Products</h2>
            <Slider {...settings}>
              {this.props.products.length > 0 && this.props.products.map((item,index) => {

                <div key={index}>
                  {item.featuredProduct === 'yes' ? <p>YES</p> : <p>NO</p>}
                    <Link to={`product_details/${item._id}`}>
                      <div className="single-product">
                        <div className="item-image">
                          <div className="product-image">
                            <img
                              src={`${imgURL}/${item.images[0]}`}
                              className="img-product"
                              alt="product pictures"
                            />
                          </div>
                          <div className="overlay">view</div>
                        </div>

                        <div className="item-desc">


                          <div className="product-name">{item.name}</div>
                          <div className="item-price">NRs. {item.price}</div>
                          <div className="item-discount">
                            <p>
                              ({item.discount && item.discount.discountValue}%
                              off)
                            </p>
                          </div>
                          <div className="item-add-to-cart">
                            <button className="btn-cart-item">
                              <Icon icon={shoppingCartOutlined} /> Add to Cart
                            </button>
                          </div>
                        </div>
                      </div>
                    </Link>
                </div>
              })}


            </Slider>  

          </div>

          {/* Computer Accessories */}
          <div className="products">
            <h2>Computer Accessories</h2>
            <ul>
              {this.props.products.length > 0 &&
                this.props.products.map((item, index) => (
                  item.category === 'Computer Accessories' &&
                  <li key={index}>

                    <Link to={`product_details/${item._id}`}>
                      <div className="single-product">
                        <div className="item-image">
                          <div className="product-image">
                            <img
                              src={`${imgURL}/${item.images[0]}`}
                              className="img-product"
                              alt="product pictures"
                            />
                          </div>
                          <div className="overlay">view</div>
                        </div>

                        <div className="item-desc">


                          <div className="product-name">{item.name}</div>
                          <div className="item-price">NRs. {item.price}</div>
                          <div className="item-discount">
                            <p>
                              ({item.discount && item.discount.discountValue}%
                              off)
                            </p>
                          </div>
                          <div className="item-add-to-cart">
                            <button className="btn-cart-item">
                              <Icon icon={shoppingCartOutlined} /> Add to Cart
                            </button>
                          </div>
                        </div>
                      </div>
                    </Link>

                  </li>
                ))}
            </ul>
          </div>

          {/* MOBILE ACCESSORIES */}
          <div className="products">
            <h2>Mobile Accessories</h2>
            <ul>
              {this.props.products.length > 0 &&
                this.props.products.map((item, index) => (
                  item.category == 'Mobile Accessories'  &&
                  <li key={index}>

                    <Link to={`product_details/${item._id}`}>
                      <div className="single-product">
                        <div className="item-image">
                          <div className="product-image">
                            <img
                              src={`${imgURL}/${item.images[0]}`}
                              className="img-product"
                              alt="product pictures"
                            />
                          </div>
                          <div className="overlay">view</div>
                        </div>

                        <div className="item-desc">


                          <div className="product-name">{item.name}</div>
                          <div className="item-price">NRs. {item.price}</div>
                          {item.discount &&
                          <div className="item-discount">
                            <p>
                              ({item.discount && item.discount.discountValue}%
                              off)
                            </p>
                          </div>
                          }
                          <div className="item-add-to-cart">
                            <button className="btn-cart-item">
                              <Icon icon={shoppingCartOutlined} /> Add to Cart
                            </button>
                          </div>
                        </div>
                      </div>
                    </Link>
                  </li>


                ))}
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (store) => ({
  products: store.product.products,
});

const mapDispatchToProps = (dispatch) => ({
  fetch: () => dispatch(fetchProduct_ac()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Landing);
