import React from "react";
import "./header.component.css";
import { Link, withRouter } from "react-router-dom";

const logout = (history) => {
  //clear localstorage and then redirect to login page
  localStorage.clear();
  history.push('/');
  //now we need to navigate
  //this will not have route props
};

const HeaderComponent = (props) => {
  console.log("props in header >>", props);
  let menu = props.isLoggedIn ? (
    <ul className="nav_list">
      <li>
        <Link to="/">Home</Link>
      </li>
      <li>
        <Link to="/profile">Profile</Link>
      </li>
      <li className="logout">
        <button className="btn btn-info logout" onClick={() => logout(props.history)}>
          Logout
        </button>
      </li>
    </ul>
  ) : (
    <ul className="nav_list">
      <li><Link to="/">Home</Link></li>
      <li><Link to="/">Login</Link></li>
      <li><Link to="/register">Register</Link></li>
    </ul>
  );
  return <div className="nav_bar">{menu}</div>;
};

export const Header = withRouter(HeaderComponent);
