import {SET_DELETING_ITEM,SET_IS_LOADING,PRODUCT_RECEIVED, PRODUCT_REMOVED,PRODUCT_DETAILS_LOADED, SET_PRODUCTID, ADD_TO_CART } from './../actions/products/type';


const initialState = {
    isLoading: false,
    productId: {},
    products: [],
    deletingItem: [],
    addedItems: [],
    total: 0
};

