import {SET_DELETING_ITEM,SET_IS_LOADING,PRODUCT_RECEIVED, PRODUCT_REMOVED } from './../actions/products/type';


const initialState = {
    isLoading: false,
    products: [],
    deletingItem: []
};

//TODO
export const productReducer = (state = initialState, action) => {
  console.log('HERE at reducer>>',action)
  switch (action.type) {
    case PRODUCT_RECEIVED:
      return{
        ...state,
        products: action.payload
      }

    case SET_IS_LOADING :
      return {
        ...state,
        isLoading: action.payload
      }

    case SET_DELETING_ITEM :
      state.deletingItem.push(action.payload);
      return{
        ...state,
      }

    case PRODUCT_REMOVED:
      state.products.splice(action.index, 1);
      // return{
      //   ...state
      // }

    default:
      return {
        ...state,
      };
  }
};


//the sole purpoose of reducer is to upadte store
