import { GET_DETAILS } from "../actions/details/types"


const initialState = {
    details: []
}

export const detailsReducer = (state = initialState, action) => {
    switch(action.type){
        case GET_DETAILS:
            return{
                ...state,
                details: action.payload
            }
    }
}
