import { GET_DETAILS, DELETE_DETAILS } from "../actions/details/types";

const initialState = {
  details: "",
};

export const detailsReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_DETAILS:
      return {
        ...state,
        details: action.payload,
      };

    case DELETE_DETAILS:
      // const details = state.details;
      // return{
      //     ...state.details.splice(action.payload, 1),
      //     details: details
      // }
      //   return {
      //     ...state,
      //     details: state.details.splice(action.payload, 1),
      //   };
      return {
        ...state,
        details: state.details.filter((i) => i.id !== action.payload.id),
      };

    default:
      return {
        ...state,
      };
  }
};
