import React from "react";
import "./header.component.css";
import history from './../history';
import { Link } from "react-router-dom";
import { Icon } from "@iconify/react";
import shoppingCartOutlined from "@iconify/icons-ant-design/shopping-cart-outlined";
import { connect } from "react-redux";
import { getNumbers, logout } from "../../../actions/products/productActions";

const imgURL = process.env.REACT_APP_IMG_URL;

// const logout = (props) => {
//   //clear localstorage and then redirect to login page
//   // e.preventDefault();
//   // window.localStorage.clear();
//   // history.push("/");
//   //now we need to navigate
//   this.props.logout();
//   //this will not have route props
// };

const HeaderComponent = (props) => {
  console.log("props in header >>", props);
  // console.log("history in header >>", history);

  // useEffect(() => ({
  //   getNumbers()
  // }, []));

  let menu = props.isLoggedIn ? (
    <div className="container">
      <div className="main-logo">
        <img src={`${imgURL}/logo.jpg`} className="img-logo" alt="logo" />
      </div>
      <ul className="nav_list">
        <li>
          <Link to="/">Home</Link>
        </li>
        <li>
          <Link to="/profile">Profile</Link>
        </li>
        <li>
          <Link to="/cart">
            My Cart <Icon icon={shoppingCartOutlined} />
            <span className="cart-num">{props.basketNumbers}</span>
          </Link>
        </li>

        <li className="logout">
          <button
            className="btn btn-info logout"
            onClick={logout()}
          >
            Logout
          </button>
        </li>
      </ul>
    </div>
  ) : (
    <div className="container">
      <div className="main-logo">
        <img src={`${imgURL}/logo.jpg`} className="img-logo" alt="logo" />
      </div>
      <ul className="nav_list">
        <li>
          <Link to="/">Home</Link>
        </li>

        <li>
          <Link to="/login">Login</Link>
        </li>
        <li>
          <Link to="/register">Register</Link>
        </li>
        <li>
          <Link to="/cart">
            My Cart <Icon icon={shoppingCartOutlined} />
            <span className="cart-num">{props.basketNumbers}</span>
          </Link>
        </li>
      </ul>
    </div>
  );
  return <div className="nav_bar">{menu}</div>;
};

const mapStateToProps = (store) => ({
  basketNumbers: store.product.basketNumbers
});

const mapDispatchToProps = (dispatch) => ({
  getNumbers: () => dispatch(getNumbers()),
  logout: () => dispatch(logout())
});

export default connect(mapStateToProps, mapDispatchToProps)(HeaderComponent);
