import React, { Component } from "react";
import { Icon } from "@iconify/react";
import plusOutlined from "@iconify/icons-ant-design/plus-outlined";
import minusOutlined from "@iconify/icons-ant-design/minus-outlined";
import closeSquareFilled from "@iconify/icons-ant-design/close-square-filled";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { fetchProduct_ac } from "../../../actions/products/productActions";

const imgURL = process.env.REACT_APP_IMG_URL;

class Cart extends Component {
  componentDidMount() {
    // this.props.fetch();
    // console.log("props in cart are>>", this.props);
    // console.log('this basket>>', this.props.products);
  }
  render() {
    console.log('cart in render>', this.props.products);

    let addedItems = 
      this.props.products && this.props.products.length > 0 ? (
        <table className="table">
          <thead>
            <tr>
              <td>S.NO.</td>
              <td>Image</td>
              <td>Name</td>
              <td>Price</td>
              <td>Brand</td>
              <td>Quantity</td>
              <td>Remove ?</td>


            </tr>
          </thead>

          <tbody>
            {this.props.products && this.props.products.length > 0  &&
              this.props.products.map((item, index) => (
                item.inCart === true &&
                <tr>
                  <td>{index + 1}</td>

                  <td>
                    <img
                      src={`${imgURL}/${item.images[0]}`}
                      alt="product_img.jpg"
                      width="150px"
                      height="150px"
                    />
                  </td>
                  <td>{item.name}</td>
                  <td>NRs. {item.price}</td>
                  <td>{item.brand}</td>

                  <td>
                    <Link to="/cart" title="Add Items">
                      <Icon icon={plusOutlined} className="btn-quantity" />
                    </Link>
                    {item.quantity}
                    <Link to="/cart" title="Substract Items">
                      <Icon icon={minusOutlined} className="btn-quantity" />
                    </Link>
                  </td>
                  <td>
                    <button className="btn-icons" title="remove">
                      <Icon icon={closeSquareFilled} color="red" />
                    </button>
                  </td>
                  <h3>Total : {this.price * item.quantity}</h3>
                </tr>


              ))}
          </tbody>
          <div className="total-cart">

          </div>
        </table>

      ) : (
        <p>Nothing</p>
      );

    return (
      <div className="container page-div">
        <h1>You have Ordered :</h1>
        <div className="cart-page">
          {addedItems}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (store) => ({
  products: store.product.products,
  basketNumbers : store.product.basketNumbers
});

const mapDispatchToProps = (dispatch) => ({
  // fetch: () => dispatch(fetchProduct_ac()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Cart);
