import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect,
} from "react-router-dom";
import { Login } from "../components/auth/login/login.component";
import { Register } from "../components/auth/register/register.component";
import { Header } from "./common/header/header.component";
import SideBar from "./common/sidebar/sidebar.component";

const NotFound = (props) => {
  return (
    <div>
      <h2>404 Not Found</h2>
      <img src="image/404.jpg" alt="404 image" className="img-fluid" />
    </div>
  );
};

const Dashboard = (props) => {
  // console.log('localStorage in dashboard >>',localStorage.getItem('token'));
  // console.log('localStorage in dashboard >>',JSON.parse(localStorage.getItem('user')));

  return (
    <div>
      <h2>Dashboard</h2>
      <p>
        Welcome to our store, plz use side nav menu or contact system
        admisnistrator for support
      </p>
    </div>
  );
};

const ProtectedRoute = ({ component: Component, ...rest }) => {
  return (
    <Route
      {...rest}
      render={(routeProps) =>
        localStorage.getItem("token") ? (
          <div>
            {/*display sidebar navbar */}
            <div className="nav_bar">
              <Header isLoggedIn={true}></Header>
            </div>
            <div className="side_bar">
              <SideBar />
            </div>
            <div className="main">
              <Component {...routeProps}></Component>
            </div>
          </div>
        ) : (
          <Redirect to="/"></Redirect>
        )
      }
    ></Route>
  );
};



const PublicRoute = ({ component: Component, ...rest }) => {
  return (
    <Route
      {...rest}
      render={(routeProps) => (
        <div>
          <div className="nav_bar">
            <Header isLoggedIn={localStorage.getItem('token') ? true : false}></Header>
          </div>
          <div className="main">
            <Component {...routeProps}></Component>
          </div>
        </div>
      )}
    ></Route>
  );
};



const Routing = (props) => {
  return (
    <Router>
      <Switch>
        <PublicRoute exact path="/" component={Login} />
        <PublicRoute path="/register" component={Register} />
        <ProtectedRoute path="/dashboard" component={Dashboard} />

        <PublicRoute component={NotFound} />
      </Switch>
    </Router>
  );
};

export default Routing;
