import React, { Component } from "react";
import notify from "../../util/notify";
import Axios from "axios";

// const BaseURL = process.env.REACT_APP_BASE_URL;

// const myAxios = Axios.create({
//     baseURL: BaseURL,
//     responseType: 'json',
//     contentType: "application/json",
//     ["Authorization"]: localStorage.getItem('token')
// })

const defaultForm = {
  sliderImage1: "",
  sliderImage2: "",
  sliderImage3: "",
  sliderImage4: "",
};
export class SliderForm extends Component {
  constructor() {
    super();
    this.state = {
      data: {
        ...defaultForm,
      },
      fileUpload: [],
    };
  }

  componentDidMount() {}

  handleChange = (e) => {
    let { files } = e.target;
    console.log("files upload results are>>> ", files);

    const { fileUpload } = this.state;
    fileUpload.push(...files);
    return this.setState({
      fileUpload,
    });
  };
  handleSubmit = (e) => {
    e.preventDefault();
    console.log("file uploaded are>>", this.state.fileUpload);
    const fd = new FormData();
    if (this.state.fileUpload && this.state.fileUpload.length) {
    //   for (let i = 0; i < this.state.fileUpload.length; i++) {
        fd.append(
          "sliderImg",
          this.state.fileUpload,
          this.state.fileUpload.name
        );
      }

    console.log("form data is >>>", fd);

    Axios.post("/slider", fd)
      .then((response) => {
        notify.showSuccess("Slider Added Successfully");
      })
      .catch((err) => {
        notify.handleError(err);
      });
  };

  render() {
    return (
      <div className="container">
        <h2>Slider Details :</h2>
        <form
          className="form-group"
          onSubmit={this.handleSubmit}
          encType="multipart/form-data"
        >
          <label>Slider Image 1</label>
          <input
            type="file"
            name="sliderImage1"
            onChange={this.handleChange}
            className="form-control"
          />

          {/* <label>Slider Image 2</label>
          <input
            type="file"
            name="sliderImage2"
            onChange={this.handleChange}
            className="form-control"
          />
          <label>Slider Image 3</label>
          <input
            type="file"
            name="sliderImage3"
            onChange={this.handleChange}
            className="form-control"
          />
          <label>Slider Image 4</label>
          <input
            type="file"
            name="sliderImage4"
            onChange={this.handleChange}
            className="form-control"
          /> */}
          <br />
          <button className="btn btn-primary" type="submit">
            submit
          </button>
        </form>
      </div>
    );
  }
}
