import React, { Component } from "react";
import { connect } from "react-redux";
import { fetchProduct_ac } from "../../../actions/products/productActions";
import { Link } from "react-router-dom";

const imgURL = process.env.REACT_APP_IMG_URL;

class Footer extends Component {
  constructor() {
    super();
    this.state = {
      names: [],
    };
  }
  componentDidMount() {
    this.fetchData();
    console.log("ptops in footer>>", this.props);
  }
  fetchData(args) {
    this.props.fetch();
  }

  handleClick = (selectedcategory) => {
    let names = this.props.products.filter(
      (pro) => pro.category === selectedcategory
    );
    this.setState({
      names,
    });
  };

  render() {
    const { products } = this.props;
    const cats = products && products.length > 0 && products.map((cat) => cat.category);

    const c = cats && cats.filter((categ, index) => cats.indexOf(categ) === index);

    // console.log("this names >>", this.state.names);

    return (
      <div className="footer-component">
        <div className="container">
          <div className="row">
            <div className="col-md-3">
              <div className="footer-item">
                <div className="footer-logo">
                  <img
                    src={`${imgURL}/logo.jpg`}
                    className="img-footer-logo"
                    alt="logo"
                  />
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="footer-item">
                <h5>Categories</h5>
                <ul>
                  {c && c.map((ca, i) => (
                    // <Link
                    //   to={{
                    //     pathname:`category/${ca}`,
                    //     state: {
                    //       names: this.state.names
                    //     }
                    // }}
                    //   onClick={() => this.handleClick(ca)
                    //   }
                    //   key={i}
                    // >
                    <li onClick={() => this.handleClick(ca)} key={i}>
                      <Link
                        to={{
                          pathname: `category/${ca}`,
                          state: {
                            names: this.state,
                          },
                        }}
                        onClick={() => this.handleClick(ca)}
                      >
                        {ca}
                      </Link>
                    </li>
                    // </Link>
                  ))}
                </ul>
              </div>
            </div>
            <div className="col-md-3"></div>
            <div className="col-md-3"></div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (store) => ({
  products: store.product.products,
});

const mapDispatchToProps = (dispatch) => ({
  fetch: () => dispatch(fetchProduct_ac()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Footer);
