import { ADD_PRODUCT_BASKET, GET_NUMBERS_BASKET, PRODUCT_RECEIVED } from "./type";
import httpClient from "../../components/util/httpClient";

export const addBasket = (productName) => dispatch => {
    console.log('Adding to basket');
    console.log('Product:', productName);


    httpClient
    .GET("/product", true)
    .then((response) => {
      response.data.forEach((item) => {
        item.inCart = false;
      });
      //dispatch action to reducer
      console.log(
        "DELAY IN CART"
      );
      dispatch({
        type: PRODUCT_RECEIVED,
        payload: response.data,
      });
      dispatch({
        type: ADD_PRODUCT_BASKET,
        payload: productName
    })
    })
    .catch((err) => {
        console.log('error is',err)
    })

}

export const getNumbers = () => dispatch => {
    console.log('get numbers in basket');
    dispatch({
        type: GET_NUMBERS_BASKET
    })
}
