import React from "react";
import "./header.component.css";
import { Link,withRouter } from 'react-router-dom';

const logout = () => {
  //clear localstorage and then redirect to login page
  localStorage.clear();
  //now we need to navigate
  //this will not have route props
}

const HeaderComponent = (props) => {
  console.log('props in header >>',props);
  let menu = props.isLoggedIn ? (
    <ul className="nav_list">
      <li><Link to="/">Home</Link></li>
      <li><Link to="/profile">Profile</Link></li>
      <li><Link to="/" className="pull_right">Logout</Link></li>
    </ul>
  ) : (
    <ul className="nav_list">
      <li>Home</li>
      <li>Login</li>
      <li>Register</li>
    </ul>
  );
  return (
    <div className="nav_bar">
       {menu}
    </div>
  );
};

export default Header = withRouter(HeaderComponent);
