import React, { Component } from "react";
import httpClient from "../../util/httpClient";
import notify from "../../util/notify";
import Loader from "../../common/loader/loader.component";
import { Icon, InlineIcon } from "@iconify/react";
import editFilled from "@iconify/icons-ant-design/edit-filled";
import closeSquareFilled from "@iconify/icons-ant-design/close-square-filled";
import './viewProduct.component.css';

export default class ViewProduct extends Component {
  constructor() {
    super();
    this.state = {
      isLoading: false,
      products: [],
    };
  }

  componentDidMount() {
    this.setState({
      isLoading: true,
    });
    httpClient
      .GET("/product", true)
      .then((response) => {
        console.log("console response >>", response);
        this.setState({
          products: response.data,
        });
      })
      .catch((err) => {
        notify.handleError(err);
      })
      .finally(() => {
        this.setState({
          isLoading: false,
        });
      });
  }

  remove(id,index){
    console.log('now remove', id);
  }

  render() {
    let content = this.state.isLoading ? (
      <Loader />
    ) : (
      <>
        <h2>View Products</h2>
        <table className="table">
          <thead>
            <tr>
              <th>S.N</th>
              <th>Name</th>
              <th>Brand</th>
              <th>Category</th>
              <th>Price</th>
              <th>Color</th>
              <th>Actions</th>
            </tr>
          </thead>

          <tbody>
            {this.state.products.map((item, index) => (
              <tr key={item._id}>
                <td>{index + 1}</td>
                <td>{item.name}</td>
                <td>{item.brand}</td>
                <td>{item.Category}</td>
                <td>{item.price}</td>
                <td>{item.color}</td>
                <td>
                  <button className="btn-icons">
                    <Icon icon={editFilled} color="lightblue" />
                  </button>
                  <button className="btn-icons" onClick={() => this.remove(item._id, index)}> 
                    <Icon icon={closeSquareFilled} color="red"/>
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </>
    );
    return content;
  }
}
