import { ADD_PRODUCT_BASKET, GET_NUMBERS_BASKET, PRODUCT_RECEIVED } from "../actions/products/type"

const initialState = {
    basketNumbers: 0,
    cartCost: 0,
    products: []
}

export default(state = initialState, action) => {
    switch(action.type){
        case PRODUCT_RECEIVED:
            return {
                ...state,
                products: action.payload,
              };
        case ADD_PRODUCT_BASKET:
            let addQuantity = {...state.products[action.payload]}
            console.log('addQuantity is>>',addQuantity);
            return{
                basketNumbers: state.basketNumbers + 1
            }

        case GET_NUMBERS_BASKET:
            return{
                ...state
            }    

        default:
            return{...state}
    }
}
