import {
  SET_DELETING_ITEM,
  SET_IS_LOADING,
  PRODUCT_RECEIVED,
  PRODUCT_REMOVED,
  PRODUCT_DETAILS_LOADED,
  SET_PRODUCTID,
} from "./../actions/products/type";

const initialState = {
  isLoading: false,
  productId: {},
  products: [],
  deletingItem: [],
  addedItems: [],
  total: 0,
};

//TODO
export const productReducer = (state = initialState, action) => {
  switch (action.type) {
    case PRODUCT_RECEIVED:
      return {
        ...state,
        products: action.payload,
      };

    case SET_IS_LOADING:
      return {
        ...state,
        isLoading: action.payload,
      };

    case SET_PRODUCTID:
      return {
        ...state,
        productId: action.payload,
      };

    case SET_DELETING_ITEM:
      return {
        ...state,
        deletingItem: action.payload,
      };

    case PRODUCT_REMOVED:
      const products = state.products;
      // products.splice(action.payload, 1)
      return {
        ...state.products.splice(action.payload, 1),
        products: products,
      };

    case PRODUCT_DETAILS_LOADED:
      return {
        ...state,
        products: action.payload,
      };


    default:
      return {
        ...state,
      };
  }
};

//the sole purpoose of reducer is to upadte store
