import React from "react";
import "./header.component.css";
import { Link, withRouter } from "react-router-dom";

const imgURL = process.env.REACT_APP_IMG_URL;

const logout = (history) => {
  //clear localstorage and then redirect to login page
  localStorage.clear();
  history.push("/");
  //now we need to navigate
  //this will not have route props
};

const HeaderComponent = (props) => {
  console.log("props in header >>", props);

  let menu = props.isLoggedIn ? (
    <div className="nav_bar">
      <ul className="nav_list">
        <div className="main-logo">
          <img src={`${imgURL}/logo.jpg`} className="img-fluid" />
        </div>
        <li>
          <Link to="/">Home</Link>
        </li>
        <li>
          <Link to="/profile">Profile</Link>
        </li>
        <li className="logout">
          <button
            className="btn btn-info logout"
            onClick={() => logout(props.history)}
          >
            Logout
          </button>
        </li>
      </ul>
    </div>
  ) : (
    <div className="nav_bar">
      <ul className="nav_list">
        <div className="main-logo">
          <img src={`${imgURL}/logo.jpg`} className="img-fluid" />
        </div>
        <li>
          <Link to="/">Home</Link>
        </li>
        <li>
          <Link to="/">Login</Link>
        </li>
        <li>
          <Link to="/register">Register</Link>
        </li>
      </ul>
    </div>
  );
  return <div className="container">{menu}</div>;
};

export const Header = withRouter(HeaderComponent);
