import React, { Component } from "react";
import { ProductForm } from "../productForm/productForm.component";
import httpClient from "../../util/httpClient";
import notify from "../../util/notify";

export default class AddProduct extends Component {
  constructor() {
    super();
    this.state = {
      isSubmitting: false,
    };
  }

  add = (data) => {
    this.setState({
      isSubmitting: true,
    });
    httpClient.POST('/product',data)
        .then(response => {
            //navigate to view product page
            notify.showInfo('Product Added Successfully');
        })
        .catch(err => {
            notify.handleError(err);
        })
        .finally(() => {
            this.setState({
                isSubmitting: false
            })
        })
  };

  render() {
    return <ProductForm title="Add Product" submitCallback={this.add} isSubmitting={this.state.isSubmitting} />;
  }
}
