import React, { Component } from 'react';

const defaultForm = {
    name: '',
    category: '',
    description: '',
    brand: '',
    color: '',
    price: '',
    weight: '',
    discountedItem: '',
    discountType: '',
    discountValue: '',
    tags: '',
    images: '',
    manuDate: '',
    expiryDate: '',
    modelNo: ''
}

export class ProductForm extends Component {

    constructor() {
        super();
        this.state = {
            data: {
                ...defaultForm
            },
            error: {
                ...defaultForm
            },
            isValidForm: false
        };
    }

    componentDidUpdate(prevProps, prevState) {
        console.log('called when either state chage or props change');
    }

    handleChange = e => {

        let { type, name, value, checked } = e.target;
        if (type === 'checkbox') {
            value = checked;
        }
        this.setState(preState => ({
            data: {
                ...preState.data,
                [name]: value
            }
        }), () => {
            this.validateForm(name);
        })
    }
    validateForm(fieldName) {
        let errMsg;
        switch (fieldName) {
            case 'category':
                errMsg = this.state.data[fieldName]
                    ? ''
                    : 'Required Field*'
                break;

            default:
                break;

        }
        this.setState(preState => ({
            error: {
                ...preState.error,
                [fieldName]: errMsg
            }
        }), () => {
            let errors = Object
                .values(this.state.error)
                .filter(err => err);
            this.setState({
                isValidForm: errors.length === 0
            })
        })
    }
    handleSubmit = e => {
        e.preventDefault();
        this.props.submitCallback(this.state.data);
    }


    render() {
        let btn = this.props.isSubmitting
            ? <button disabled className="btn btn-info">submitting...</button>
            : <button disabled={!this.state.isValidForm} type="submit" className="btn btn-info">submit</button>

        let discountContent = this.state.data.discountedItem
            ? <>
                <label>Discount Type</label>
                <input type="text" className="form-control" placeholder="Discount Type" name="discountType" onChange={this.handleChange}></input>
                <label>Discount Value</label>
                <input type="text" className="form-control" placeholder="Discount Value" name="discountValue" onChange={this.handleChange}></input>
            </>
            : '';

        return (
            <>
                <h2>{this.props.title}</h2>
                <form className="form-group" onSubmit={this.handleSubmit}>
                    <label>Name</label>
                    <input type="text" className="form-control" placeholder="Name" name="name" onChange={this.handleChange}></input>
                    <label>Description</label>
                    <input type="text" className="form-control" placeholder="Description" name="description" onChange={this.handleChange}></input>
                    <label>Category</label>
                    <input type="text" className="form-control" placeholder="Category" name="category" onChange={this.handleChange}></input>
                    <p className="error">{this.state.error.category}</p>
                    <label>Brand</label>
                    <input type="text" className="form-control" placeholder="Brand" name="brand" onChange={this.handleChange}></input>
                    <label>Price</label>
                    <input type="number" className="form-control" placeholder="Price" name="price" onChange={this.handleChange}></input>
                    <label>Color</label>
                    <input type="text" className="form-control" placeholder="Color" name="color" onChange={this.handleChange}></input>
                    <label>Model No</label>
                    <input type="text" className="form-control" placeholder="Model No" name="modelNo" onChange={this.handleChange}></input>
                    <label>Manu. Date</label>
                    <input type="date" className="form-control" name="manuDate" onChange={this.handleChange}></input>
                    <label>Expiry Date</label>
                    <input type="date" className="form-control" name="expiryDate" onChange={this.handleChange}></input>
                    <label>Tags</label>
                    <input type="text" className="form-control" placeholder="Tags" name="tags" onChange={this.handleChange}></input>
                    <label>Weight</label>
                    <input type="text" className="form-control" placeholder="Weight" name="weight" onChange={this.handleChange}></input>
                    <input type="checkbox" name="discountedItem" onChange={this.handleChange}></input>
                    <label>Discounted Item</label>
                    <br></br>
                    {discountContent}
                    <br />
                    {btn}
                </form>
            </>
        )
    }
}
