import React, { Component } from 'react';
import SubmitButton from '../../common/submitButton/submitButton.component';



const defaultForm = {
    name: "",
    category: "",
    brand: "",
    color: "",
    minPrice: "",
    maxPrice: "",
    fromDate: "",
    toDate: "",
    discountedItem: "",
    tags: "",

  };

export class SearchProduct extends Component {
    constructor(){
        super();
        this.state = {
            data: {...defaultForm},
            error: {...defaultForm},
            isSubmitting: false,
            isValidForm:false 
        }
    }

    handleChange = (e) => {
        let { type, name, value, checked } = e.target;
        if (type === "checkbox") {
          value = checked;
        }


        this.setState(
          (preState) => ({
            data: {
              ...preState.data,
              [name]: value,
            },
          }),
          () => {
            this.validateForm(name);
          }
        );
      };
      validateForm(fieldName) {
        let errMsg;
        switch (fieldName) {
          case "category":
            errMsg = this.state.data[fieldName] ? "" : "Required Field*";
            break;

          default:
            break;
        }
        this.setState(
          (preState) => ({
            error: {
              ...preState.error,
              [fieldName]: errMsg,
            },
          }),
          () => {
            let errors = Object.values(this.state.error).filter((err) => err);
            this.setState({
              isValidForm: errors.length === 0,
            });
          }
        );
      }
      handleSubmit = (e) => {
        e.preventDefault();
        //search
      };


    render(){
        // let btn = this.props.isSubmitting ? (
        //     <button disabled className="btn btn-info">
        //       submitting...
        //     </button>
        //   ) : (
        //     <button
        //       disabled={!this.state.isValidForm}
        //       type="submit"
        //       className="btn btn-info"
        //     >
        //       submit
        //     </button>
        //   );

        return(
            <>
                <h2>Search Products Here</h2>
                <form className="form-group" onSubmit={this.handleSubmit}>
          {/* <label>Name</label>
          <input
            type="text"
            className="form-control"
            placeholder="Name"
            name="name"
            value={this.state.data.name}
            onChange={this.handleChange}
          ></input> */}

          <label>Category</label>
          <input
            type="text"
            className="form-control"
            placeholder="Category"
            name="category"
            value={this.state.data.category}
            onChange={this.handleChange}
          ></input>
          <p className="error">{this.state.error.category}</p>
          <label>Brand</label>
          <input
            type="text"
            className="form-control"
            placeholder="Brand"
            name="brand"
            value={this.state.data.brand}
            onChange={this.handleChange}
          ></input>
          <label>Min Price</label>
          <input
            type="number"
            className="form-control"
            placeholder="Min Price"
            name="minPrice"
            value={this.state.data.minPrice}
            onChange={this.handleChange}
          ></input>
          <label>Max Price</label>
          <input
            type="number"
            className="form-control"
            placeholder="Max Price"
            name="maxPrice"
            value={this.state.data.maxPrice}
            onChange={this.handleChange}
          ></input>

          <label>Color</label>
          <input
            type="text"
            className="form-control"
            placeholder="Color"
            name="color"
            value={this.state.data.color}
            onChange={this.handleChange}
          ></input>

          <label>From Date</label>
          <input
            type="date"
            className="form-control"
            name="fromDate"
            onChange={this.handleChange}
          ></input>
          <label>To Date</label>
          <input
            type="date"
            className="form-control"
            name="toDate"
            onChange={this.handleChange}
          ></input>
          <label>Tags</label>
          <input
            type="text"
            className="form-control"
            placeholder="Tags"
            name="tags"
            value={this.state.data.tags}
            onChange={this.handleChange}
          ></input>
          <label>Weight</label>
          <input
            type="text"
            className="form-control"
            placeholder="Weight"
            name="weight"
            value={this.state.data.weight}
            onChange={this.handleChange}
          ></input>
          <br />
          <SubmitButton isSubmitting={this.props.isSubmitting} isValidForm={this.state.isValidForm} />
        </form>
            </>
        )
    }
}
