import React, { Component } from "react";
import * as io from "socket.io-client";
import { Icon } from '@iconify/react';
import sendOutlined from '@iconify/icons-ant-design/send-outlined';
import "./chat.component.css";

export default class ChatComponent extends Component {
  constructor() {
    super();
    this.state = {
        message: '',
        senderId: '',
        receiverId: '',
        time: '',
        senderName:'',
        receiverName: ''
    };
  }
  componentDidMount() {
    this.socket = io.connect(process.env.REACT_APP_SOCKET_URL);
    this.runSocket();
    this.currentUser = JSON.parse(localStorage.getItem('user'));
    this.setState({
        senderName: this.currentUser.username
    })
  }

  handleChange = (e) => {
      const{name, value} = e.target;
      this.setState({
          [name]: value
      })
  };

  handleSubmit = (e) => {
    e.preventDefault();
    this.socket.emit("new-msg", "hello");
  };

  runSocket() {
    this.socket.on("hi", (data) => {
      console.log("data in hi event at client programme", data);
      this.socket.emit("hello", "I am Client");
    });
  }

  render() {
    return (
      <div className="">
        <h2>Let's Chat</h2>
        <div className="row">
          <div className="col-md-6">
            <ins>Messages</ins>
            <div className="chat_block">
              <div className="sender-name">
                 <p>Chat with {this.state.senderName}</p>
              </div>
              <form className="form-group form-div" onSubmit={this.handleSubmit}>
                <input
                  type="text"
                  name="message"
                  className="form-control input-class"
                  placeholder="your message here..."
                  onChange={this.handleChange}
                />
                <button className="btn-send" type="submit">
                <Icon icon={sendOutlined} />
                </button>
              </form>
            </div>
          </div>
          <div className="col-md-6">
            <ins>Users</ins>
            <div className="chat_block"></div>
          </div>
        </div>
      </div>
    );
  }
}
