import React, { Component } from 'react';
import * as io from 'socket.io-client';

export default class ChatComponent extends Component{
    constructor(){
        super();
    }
    componentDidMount(){
        this.socket = io.connect(process.env.REACT_APP_SOCKET_URL);
        this.runSocket();
    }

    runSocket(){
        this.socket.on('hi', (data) => {
            console.log('data in hi event at client programme',data);
            this.socket.emit('hello','I am Client');
        })
    }

    render(){
        return(
            <>
                <h2>Let's Chat</h2>
                <div className="row">
                    <div className="col-md-6">
                        <ins>Messages</ins>
                        <div className="chat_block">

                        </div>
                    </div>
                    <div className="col-md-6">
                        <ins>Users</ins>
                        <div className="chat_block">

                        </div>
                    </div>
                </div>
            </>
        )
    }
}
