import React from 'react';
import { Login } from "../components/auth/login/login.component";
import { Register } from "../components/auth/register/register.component";
import { BrowserRouter as Router, Route, Switch, Redirect } from "react-router-dom";
import Header from './common/header/header.component';

const NotFound = (props) => {
  return (
    <div>
      <h2>404 Not Found</h2>
      {/* <img src="image/404.jpg" alt="404 image" className="img-fluid" /> */}
    </div>
  );
};

const Dashboard = (props) => {
  // console.log('localStorage in dashboard >>',localStorage.getItem('token'));
  // console.log('localStorage in dashboard >>',JSON.parse(localStorage.getItem('user')));

  return (
    <div>
      <h2>Dashboard</h2>
      <p>Welcome to our store, plz use side nav menu or contact system admisnistrator for support</p>
    </div>
  )
}

const ProtectedRoute = ({component:Component, ...rest}) => {
  return <Route {...rest} render={(routeProps) => (
      localStorage.getItem('token')
      ? <div>
        {/*display sidebar navbar */}
          <Component {...routeProps}></Component>
      </div>
      : <Redirect to="/"></Redirect>
    )}>

    </Route>
}

const Routing = (props) => {
  return (
    <Router>
      <Header isLoggedIn={true} />
      <Switch>
        <Route exact path="/" component={Login} />
        <Route path="/register" component={Register} />
        <ProtectedRoute path="/dashboard" component={Dashboard} />

        <Route component={NotFound} />
      </Switch>
    </Router>
  );
};

export default Routing;
