import React, { Component } from 'react';
import SliderHome from '../common/slider/sliderHome.component';
import { connect } from 'react-redux';
import { fetchProduct_ac } from '../../actions/products/productActions';
import SingleItem from '../common/singleItem/singleItem.component';


class Landing extends Component{
    constructor(){
        super();
    }
    componentDidMount(){
        // console.log('products in state', this.state);
        // console.log('products in props', this.props);
        this.props.fetch();
        console.log('products >>>>', this.props.products.category)
    }
    render(){
        return(
            <div className="">
                <SliderHome />
                {this.props.products.length > 0 && this.props.products.map((item,index) => (
                    <SingleItem />
                ))}
            </div>
        )
    }
}

const mapStateToProps = (store) => ({
    products: store.product.products
})

const mapDispatchToProps = dispatch => ({
    fetch : () => dispatch(fetchProduct_ac())
})

export default connect(mapStateToProps, mapDispatchToProps)(Landing);
