import React, { Component } from "react";
import { Icon } from "@iconify/react";
import plusOutlined from "@iconify/icons-ant-design/plus-outlined";
import minusOutlined from "@iconify/icons-ant-design/minus-outlined";
import closeSquareFilled from "@iconify/icons-ant-design/close-square-filled";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import Recipe from './recipe.component'

const imgURL = process.env.REACT_APP_IMG_URL;



class Cart extends Component {

    handleRemove = (id) => {
        this.props.removeItem(id);
    }

    handleAddQuantity = (id) => {
        this.props.addQuantity(id);
    }

    handleSubtractQuantity = id => {
        this.props.subtractQuantity(id);
    }


  render() {
    let addedItems = this.props.items.length > 0 ?
    <table className="table">
            <thead></thead>

            <tbody>
              {this.props.items.length > 0 &&
                this.props.items.map((item) => (
                  <tr key={item.id}>
                    <td>{item.id + 1}</td>

                    <td>
                      <img
                        src={`${imgURL}/${item.images[0]}`}
                        alt="product_img.jpg"
                        width="150px"
                        height="150px"
                      />
                    </td>
                    <td>{item.name}</td>
                    <td>NRs. {item.price}</td>
                    <td>{item.brand}</td>
                    <td>{item.quantity}</td>

                    <td>
                      <Link to="/cart" onClick={() => {this.handleAddQuantity(item.id)}}>
                        <Icon icon={plusOutlined} />
                      </Link>
                      <Link to="/cart" onClick={() => {this.handleSubtractQuantity(item.id)}}>
                        <Icon icon={minusOutlined} />
                      </Link>
                    </td>
                    <td>
                      <button
                        className="btn-icons"
                        title="remove"
                        onClick={() => {this.handleRemove(item.id)}}
                      >
                        <Icon icon={closeSquareFilled} color="red" />
                      </button>
                    </td>
                  </tr>
                ))}
            </tbody>
          </table>
          : <p>Nothing</p>

    return (
      <div className="container page-div">
        <h1>You have Ordered :</h1>
        <div className="cart-page">
            {addedItems}
        </div>
        <Recipe />
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  items: state.addedItems,
});

const mapDispatchToProps = (dispatch) => ({
  removeItem: (id)=>{dispatch(removeItem(id))},
  addQuantity: (id)=>{dispatch(addQuantity(id))},
  subtractQuantity: (id)=>{dispatch(subtractQuantity(id))}
})

export default connect(mapStateToProps, mapDispatchToProps)(Cart);
