import {SET_IS_FETCHING,SET_IS_LOADING,PRODUCT_RECEIVED } from './../actions/products/type';


const initialState = {
    isLoading: false,
    products: []
};

//TODO
export const productReducer = (state = initialState, action) => {
  console.log('HERE at reducer>>',action)
  switch (action.type) {
    case 'PRODUCTS_RECEIVED':
      return{
        ...state,
        products: action.payload
      }

    case "GET_PRODUCTS":
      return {
        ...state,
      };

    default:
      return {
        ...state,
      };
  }
};
