import React, { Component } from "react";

const defaultForm = {
  name: "",
  category: "",
  description: "",
  brand: "",
  color: "",
  price: "",
  weight: "",
  discountedItem: "",
  discountType: "",
  discountValue: "",
  tags: "",
  images: "",
  manuDate: "",
  expiryDate: "",
  modelNo: "",
};

export class ProductForm extends Component {
  constructor() {
    super();
    this.state = {
      data: {
        ...defaultForm,
      },
      error: {
        ...defaultForm,
      },
      isSubmitting: false,
      isValidForm: false,
    };
  }

  handleChange = (e) => {
    let { name, value, type, checked } = e.target;
    if (type === "checkbox") {
      value = checked;
    }
    this.setState(
      (preState) => ({
        data: {
          ...preState,
          [name]: value,
        },
      }),
      () => {
        this.validateForm(name);
      }
    );
  };

  validateForm(fieldName) {
    let errMsg;
    switch (fieldName) {
      case "category":
        errMsg = this.state.data[fieldName] ? "" : "Required Field";
        break;
    }
  }

  handleSubmit = (e) => {
    e.preventDefault();
  };

  render() {
    let btn = this.state.isSubmitting ? (
      <button disabled class="btn btn-info">
        Submitting...
      </button>
    ) : (
      <button
        disabled={!this.state.isValidForm}
        type="submit"
        className="btn btn-primary"
      >
        Submit
      </button>
    );

    let discountContent = this.state.data.discountedItem ? (
      <>
        <label>Discounted Type</label>
        <input
          type="text"
          name="discountedType"
          className="form-control"
          placeholder="Discounted Type"
          onChange={this.handleChange}
        />
        <label>Discounted Value</label>
        <input
          type="text"
          name="discountedValue"
          className="form-control"
          placeholder="Discounted Value"
          onChange={this.handleChange}
        />
      </>
    ) : (
      ""
    );

    return (
      <>
        <h2>Add Product</h2>
        <form className="form-group" onSubmit={this.handleSubmit}>
          <label>Name</label>
          <input
            type="text"
            name="name"
            className="form-control"
            placeholder="Name"
            onChange={this.handleChange}
          />
          <label>Description</label>
          <input
            type="text"
            name="description"
            className="form-control"
            placeholder="Description"
            onChange={this.handleChange}
          />
          <label>Category</label>
          <input
            type="text"
            name="category"
            className="form-control"
            placeholder="Category"
            onChange={this.handleChange}
          />
          <label>Brand</label>
          <input
            type="text"
            name="brand"
            className="form-control"
            placeholder="Brand"
            onChange={this.handleChange}
          />
          <label>Price</label>
          <input
            type="text"
            name="price"
            className="form-control"
            placeholder="Price"
            onChange={this.handleChange}
          />
          <label>Color</label>
          <input
            type="text"
            name="color"
            className="form-control"
            placeholder="Color"
            onChange={this.handleChange}
          />
          <label>Model no</label>
          <input
            type="text"
            name="modelno"
            className="form-control"
            placeholder="Model no"
            onChange={this.handleChange}
          />
          <label>Manu Date</label>
          <input
            type="date"
            name="manuDate"
            className="form-control"
            onChange={this.handleChange}
          />
          <label>Expiry Date</label>
          <input
            type="date"
            name="expiryDate"
            className="form-control"
            onChange={this.handleChange}
          />
          <label>Tags</label>
          <input
            type="text"
            name="tags"
            className="form-control"
            placeholder="Tags"
            onChange={this.handleChange}
          />
          <label>Weight</label>
          <input
            type="text"
            name="weight"
            className="form-control"
            placeholder="Weight"
            onChange={this.handleChange}
          />
          <input
            type="checkbox"
            name="discountedItem"
            className=""
            placeholder="Discounted Item"
            onChange={this.handleChange}
          />
          <label>Discounted Item</label>
          <br />

          <br />
          {btn}
        </form>
      </>
    );
  }
}
