import React, { useEffect, useState, Component } from "react";
import { connect } from "react-redux";
import { fetchProduct_ac } from "../../../actions/products/productActions";
const imgURL = process.env.REACT_APP_IMG_URL;

class Footer extends Component {
  componentDidMount() {
    this.props.fetch();
    console.log("this.props in footer are>>>", this.props);
    // let cats = [];
    const cats = [...new Set(this.props.products.map(cat => cat.category))]

  }

  render() {
    console.log("my unique category are", this.propscats);

    return (
      <div className="footer-component">
        <div className="container">
          <div className="row">
            <div className="col-md-3">
              <div className="footer-item">
                <div className="footer-logo">
                  <img
                    src={`${imgURL}/logo.jpg`}
                    className="img-footer-logo"
                    alt="logo"
                  />
                </div>
              </div>
            </div>
            <div className="col=-md-3">
              <div className="footer-item">
                <h5>Categories</h5>
                <ul>
                  {/* {this.state.cats.map((categs, i) => (
                    <li key={i}>{categs}</li>
                  ))} */}
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (store) => ({
  products: store.product.products,
});

const mapDispatchToProps = (dispatch) => ({
  fetch: () => dispatch(fetchProduct_ac()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Footer);
