import React, { Component } from "react";
import httpClient from "../../util/httpClient";
import notify from "../../util/notify";
import Loader from "../../common/loader/loader.component";
import { Icon, InlineIcon } from "@iconify/react";
import editFilled from "@iconify/icons-ant-design/edit-filled";
import closeSquareFilled from "@iconify/icons-ant-design/close-square-filled";
import "./viewProduct.component.css";

export default class ViewProduct extends Component {
  constructor() {
    super();
    this.state = {
      isLoading: false,
      products: [],
      isDeleting: false,
    };
  }

  componentDidMount() {
    this.setState({
      isLoading: true,
    });
    httpClient
      .GET("/product", true)
      .then((response) => {
        console.log("console response >>", response);
        this.setState({
          products: response.data,
        });
      })
      .catch((err) => {
        notify.handleError(err);
      })
      .finally(() => {
        this.setState({
          isLoading: false,
        });
      });
  }

  edit(id){
    this.props.history.push(`/edit_product/${id}`);
  }

  remove(id, index) {
    this.setState({
      isDeleting: true,
    });
    console.log("now remove", id);
    // eslint-disable-next-line no-restricted-globals
    let confirmation = confirm("Are you sure to remove?");
    if (confirmation) {
      httpClient
        .DELETE(`/product/${id}`, true)
        .then((response) => {
          notify.showInfo("Product Removed");
          const { products } = this.state;
          products.splice(index, 1);
          this.setState({
            products,
          });
        })
        .catch((err) => {
          notify.handleError(err);
        })
        .finally(() => {
          this.setState({
            isDeleting: false,
          });
        });
    }
  }

  render() {
    let deleting = this.state.isDeleting ?
      <p>deleting...</p>
    : <button
    className="btn-icons"
    title="delete"
    onClick={() => this.remove(this.state._id,this.state.index)}
  >
    <Icon icon={closeSquareFilled} color="red" />
  </button>


    let content = this.state.isLoading ? (
      <Loader />
    ) : (
      <>
        <h2>View Products</h2>
        <table className="table">
          <thead>
            <tr>
              <th>S.N</th>
              <th>Name</th>
              <th>Brand</th>
              <th>Category</th>
              <th>Price</th>
              <th>Color</th>
              <th>Actions</th>
            </tr>
          </thead>

          <tbody>
            {this.state.products.map((item, index) => (
              <tr key={item._id}>
                <td>{index + 1}</td>
                <td>{item.name}</td>
                <td>{item.brand}</td>
                <td>{item.Category}</td>
                <td>{item.price}</td>
                <td>{item.color}</td>
                <td>
                  <button className="btn-icons" title="edit" onClick={() => this.edit(item._id)} >
                    <Icon icon={editFilled} color="lightblue" />
                  </button>
                  {/* <button
                    className="btn-icons"
                    title="delete"
                    onClick={() => this.remove(item._id, index)}
                  >
                    <Icon icon={closeSquareFilled} color="red" />
                  </button> */}
                  {deleting}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </>
    );
    return content;
  }
}
