import React, { Component } from "react";
import { Icon } from "@iconify/react";
import plusOutlined from "@iconify/icons-ant-design/plus-outlined";
import minusOutlined from "@iconify/icons-ant-design/minus-outlined";
import closeSquareFilled from "@iconify/icons-ant-design/close-square-filled";
import { Link } from "react-router-dom";
import { connect } from "react-redux";

const imgURL = process.env.REACT_APP_IMG_URL;



class Cart extends Component {

componentDidMount(){
  this.props.fetch();
  console.log('props in cart are>>', this.props)
}
  render() {
    let addedItems = this.props.items && this.props.items.length > 0 ?
    <table className="table">
            <thead></thead>

            <tbody>
              {this.props.items.length > 0 &&
                this.props.items.map((item,index) => (
                  <tr key={index}>
                    <td>{index + 1}</td>

                    <td>
                      <img
                        src={`${imgURL}/${item.images[0]}`}
                        alt="product_img.jpg"
                        width="150px"
                        height="150px"
                      />
                    </td>
                    <td>{item.name}</td>
                    <td>NRs. {item.price}</td>
                    <td>{item.brand}</td>
                    <td>{item.quantity}</td>

                    <td>
                      <Link to="/cart" >
                        <Icon icon={plusOutlined} />
                      </Link>
                      <Link to="/cart" >
                        <Icon icon={minusOutlined} />
                      </Link>
                    </td>
                    <td>
                      <button
                        className="btn-icons"
                        title="remove"
                      >
                        <Icon icon={closeSquareFilled} color="red" />
                      </button>
                    </td>
                  </tr>
                ))}
            </tbody>
          </table>
          : <p>Nothing</p>

    return (
      <div className="container page-div">
        <h1>You have Ordered :</h1>
        <div className="cart-page">
            {addedItems}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (store) => ({
  products: store.product.products
});

const mapDispatchToProps = (dispatch) => ({
  addBasket: () => dispatch(addBasket())
})

export default connect(mapStateToProps, mapDispatchToProps)(Cart);
