import React, { Component } from "react";
import { connect } from "react-redux";
import { fetchProduct_ac } from "../../actions/products/productActions";
import httpClient from "../util/httpClient";

class Category extends Component {
  constructor() {
    super();
    this.state = {
      appProducts: [],
      categories: ''
    };
  }
  componentDidMount() {
    this.props.fetch();


  }


  render() {
    const { products } = this.props;
    const cats = products.map((cat) => cat.category);

    const c = cats.filter((categ, index) => cats.indexOf(categ) === index);

    c.map((item) => console.log("products in props", item));



    return <h2>Catgeory Page</h2>;
  }
}

const mapStateToProps = (store) => ({
  products: store.product.products,
});

const mapDispatchToProps = (dispatch) => ({
  fetch: () => dispatch(fetchProduct_ac),
});

export default connect(mapStateToProps, mapDispatchToProps)(Category);
