import { ADD_TO_CART, REMOVE_ITEM } from "../actions/products/type";

const initialState = { items: [] };

export default function (state = initialState, action) {
  switch (action.type) {
    case ADD_TO_CART:
      return {
        items: action.payload.cartItems,
      };
    case REMOVE_ITEM:
      return {
        items: action.payload.cartItems,
      };
    default:
      return state;
  }
}
