import React, { Component } from "react";
import notify from "../../util/notify";
import httpClient from "../../util/httpClient";


const defaultForm = {
  sliderImage1: "",
  // sliderImage2: "",
  // sliderImage3: "",
  // sliderImage4: "",
};
export class SliderForm extends Component {
  constructor() {
    super();
    this.state = {
      data: {
        ...defaultForm,
      },
      fileUpload: [],
    };
  }

  componentDidMount() {}

  handleChange = (e) => {
    let { files } = e.target;
    console.log("files upload results are>>> ", files);

    const { fileUpload } = this.state;
    fileUpload.push(...files);
    return this.setState({
      fileUpload,
    });
  };
  handleSubmit = (e) => {
    e.preventDefault();
    console.log("file uploaded are>>", this.state.fileUpload);
    // const fd = new FormData();
    //     fd.append(
    //       "sliderImg",
    //       this.state.fileUpload[0],
    //       this.state.fileUpload[0].name
    //     );

    // console.log("form data is >>>", fd);

    httpClient.UPLOADSLIDER('POST','/slider',this.state.fileUpload)
      .then((response) => {
        notify.showSuccess("Slider Added Successfully");
      })
      .catch((err) => {
        notify.handleError(err);
      });
  };

  render() {
    return (
      <div className="container">
        <h2>Slider Details :</h2>
        <form
          className="form-group"
          onSubmit={this.handleSubmit}
          encType="multipart/form-data"
        >
          <label>Slider Image 1</label>
          <input
            type="file"
            name="sliderImage1"
            onChange={this.handleChange}
            className="form-control"
          />

          {/* <label>Slider Image 2</label>
