import React, { Component } from "react";

const defaultForm = {
  name: "",
  category: "",
  description: "",
  brand: "",
  color: "",
  price: "",
  weight: "",
  discountedItem: "",
  discountType: "",
  discountValue: "",
  tags: "",
  images: "",
  manuDate: "",
  expiryDate: "",
  modelNo: "",
};

export class ProductForm extends Component {
  constructor() {
    super();
    this.state = {
      data: {
        ...defaultForm,
      },
      error: {
        ...defaultForm,
      },
      isValidForm: false,
    };
  }

  componentDidUpdate(prevProps, prevState){
      console.log('called when either state or props change');
  }


  handleChange = (e) => {
    let { name, value, type, checked } = e.target;
    if (type === "checkbox") {
      value = checked;
    }
    this.setState(
      (preState) => ({
        data: {
          ...preState,
          [name]: value,
        },
      }),
      () => {
        this.validateForm(name);
      }
    );
  };

  validateForm(fieldName) {
    let errMsg;
    switch (fieldName) {
      case "category":
        errMsg = this.state.data[fieldName] ? "" : "Required Field";
        break;

      default:
          break;  
    }
    this.setState(preState => ({
        error: {
            ...preState.error,
            [fieldName]:errMsg
        }
    }), () => {
        let errors = Object.values(this.state.error).filter(err => err);
        this.setState({
            isValidForm: errors.length === 0
        })
    })

  }

  handleSubmit = (e) => {
    e.preventDefault();
    this.props.submitCallback(this.state.data);
  };

  render() {
    let btn = this.props.isSubmitting ? (
      <button disabled class="btn btn-info">
        Submitting...
      </button>
    ) : (
      <button
        disabled={!this.state.isValidForm}
        type="submit"
        className="btn btn-primary"
      >
        Submit
      </button>
    );

    let discountContent = this.state.data.discountedItem ? (
      <>
        <label>Discounted Type</label>
        <input
          type="text"
          name="discountedType"
          className="form-control"
          placeholder="Discounted Type"
          onChange={this.handleChange}
        />
        <label>Discounted Value</label>
        <input
          type="text"
          name="discountedValue"
          className="form-control"
          placeholder="Discounted Value"
          onChange={this.handleChange}
        />
      </>
    ) : (
      ""
    );

    return (
      <>
        <h2>{this.props.title}</h2>
        <form className="form-group" onSubmit={this.handleSubmit}>
          <label>Name</label>
          <input
            type="text"
            name="name"
            className="form-control"
            placeholder="Name"
            onChange={this.handleChange}
          />
          <label>Description</label>
          <input
            type="text"
            name="description"
            className="form-control"
            placeholder="Description"
            onChange={this.handleChange}
          />
          <label>Category</label>
          <input
            type="text"
            name="category"
            className="form-control"
            placeholder="Category"
            onChange={this.handleChange}
          />
          <p className="error">{this.state.error.category}</p>
          <label>Brand</label>
          <input
            type="text"
            name="brand"
            className="form-control"
            placeholder="Brand"
            onChange={this.handleChange}
          />
          <label>Price</label>
          <input
            type="text"
            name="price"
            className="form-control"
            placeholder="Price"
            onChange={this.handleChange}
          />
          <label>Color</label>
          <input
            type="text"
            name="color"
            className="form-control"
            placeholder="Color"
            onChange={this.handleChange}
          />
          <label>Model no</label>
          <input
            type="text"
            name="modelno"
            className="form-control"
            placeholder="Model no"
            onChange={this.handleChange}
          />
          <label>Manu Date</label>
          <input
            type="date"
            name="manuDate"
            className="form-control"
            onChange={this.handleChange}
          />
          <label>Expiry Date</label>
          <input
            type="date"
            name="expiryDate"
            className="form-control"
            onChange={this.handleChange}
          />
          <label>Tags</label>
          <input
            type="text"
            name="tags"
            className="form-control"
            placeholder="Tags"
            onChange={this.handleChange}
          />
          <label>Weight</label>
          <input
            type="text"
            name="weight"
            className="form-control"
            placeholder="Weight"
            onChange={this.handleChange}
          />
          <input
            type="checkbox"
            name="discountedItem"
            className=""
            placeholder="Discounted Item"
            onChange={this.handleChange}
          />
          <label>Discounted Item</label>
          <br />

          <br />
          {btn}
        </form>
      </>
    );
  }
}
