import React, { Component } from "react";
// import { connect } from "react-redux";
// import { fetchProduct_ac } from "../../../actions/products/productActions";
const imgURL = process.env.REACT_APP_IMG_URL;

class Footer extends Component {
  componentDidMount() {
    console.log('props in footer >>', this.props.products)

    if(this.props.incomingData){
        return this.setState({
          products: this.props.incomingData
        })
      }
  }

  render() {


    return (
      <div className="footer-component">
        <div className="container">
          <div className="row">
            <div className="col-md-3">
              <div className="footer-item">
                <div className="footer-logo">
                  <img
                    src={`${imgURL}/logo.jpg`}
                    className="img-footer-logo"
                    alt="logo"
                  />
                </div>
              </div>
            </div>
            <div className="col=-md-3">
              <div className="footer-item">
                <h5>Categories</h5>
                <ul>
                  {[...new Set(this.props.products && this.props.products.map(cat => cat.category))].map((categs, i) => (
                    <li key={i}>{categs}</li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

// const mapStateToProps = (store) => ({
//   products: store.product.products,
// });

// const mapDispatchToProps = (dispatch) => ({
//   fetch: () => dispatch(fetchProduct_ac()),
// });

// export default connect(mapStateToProps, mapDispatchToProps)(Footer);

export default Footer;
