import React, { Component } from 'react';

export class Login extends Component {
    constructor(){
        super();
        this.state = {
            username: '',
            password: '',
            remember_me: false
        };
        this.handleChanges = this.handleChanges.bind(this);      //bind gareko 
    }

    handleChanges(event){
        // console.log('event >>',event);
        // console.log('event target >>',event.target);
        const {name,value} = event.target;
        console.log('name >>',name);
        console.log('value >>',value);
        this.setState({
            [name] : value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        console.log('Check state>>',this.state);
        //http with username and password
    }

    render(){
        return (
            <div>
                <h2>Login</h2>
                <p>Please login to start your session</p>
                <form className="form-group">
                    <label htmlFor="username">Username</label>
                    <input type="text" placeholder="Username" className="form-control" id="username" name="username" onChange={this.handleChanges} />
                    <label htmlFor="password">Password</label>
                    <input type="password" placeholder="Password" className="form-control" id="password" name="password" onChange={this.handleChanges} />
                    <button className="btn btn-primary mt-2" onClick={this.handleSubmit}>Login</button>
                </form>
                <p>Don't have an account?</p>
                <p>Register <a href="#">Here</a>
                <span className="pull_right">
                    <a href="#">forgot password?</a>
                </span></p>
            </div>
        )
    }
}
