import React, { Component } from "react";
import { Formik } from "formik";
import httpClient from "../../util/httpClient";
import notify from "../../util/notify";
import {Link} from 'react-router-dom';

export class ForgotPassword extends Component {
  constructor() {
    super();
    this.state = {
        data: {
            email: ''
        },
        error: {
            email: ''
        },
        isSubmitting: false
    };
  }

  handleChange = e => {
      const { name, value} = e.target;
      this.setState({
          data: {
              [name] : value
          }
      })
  }

  handleSubmit = e => {
      e.preventDefault();
      this.setState({
          isSubmitting: true
      })
      httpClient.POST('/auth/forgot_password', this.state.data)
        .then(data => {
            notify.showInfo('Password reset link sent to your email, please check your inbox');
            this.props.history.push('/');
        })
        .catch(err => {
            notify.handleError(err);
            this.setState({
                isSubmitting:false
            })
        })
  }

  render() {
    return (
      <div className="public_route">
        <h2>Forgot Password</h2>
        <p>Please provide your email address to reset your password</p>
        <Formik
          initialValues={{ email: "" }}
          validate={(values) => {
            const errors = {};
            if (!values.email) {
              errors.email = "Required Email";
            } else if (
              !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
            ) {
              errors.email = "Invalid Email Address";
            }
            return errors;
          }}
          onSubmit={(values, { setSubmitting }) => {
            setTimeout(() => {
              alert(JSON.stringify(values, null, 2));
              setSubmitting(false);
            }, 400);
          }}
        >
        {({
         values,
         errors,
         handleChange,
         handleSubmit,
         isSubmitting,
         /* and other goodies */
       }) => (
         <form onSubmit={handleSubmit} className="form-group">
             <br/>
             <label>Email Address</label>
           <input
           className="form-control"
             type="email"
             name="email"
             placeholder="Email"
             onChange={handleChange}
             value={values.email}
           />
           <p className="error">{errors.email && errors.email}</p>
           <br/>
           <button className="btn btn-primary" type="submit" disabled={isSubmitting}>
             Submit
           </button>
         </form>
       )}
       </Formik>
       <div className="pull_right">
            Back to<Link to="/"> Login</Link>
        </div>
      </div>
    )
  }
}
