import React, { Component } from "react";
import { ProductForm } from "../productForm/productForm.component";
import httpClient from "../../util/httpClient";
import notify from "../../util/notify";
import { connect } from "react-redux";
import { addProduct_ac } from "../../../actions/products/productActions";


class AddProduct extends Component {

    constructor() {
        super();
        // this.state = {
        //     isSubmitting: false
        // };
    }


    add = (data,files) => {
        this.setState({
            isSubmitting: true
        })
        this.props.addProduct_ac(data,files);
    }
    render() {
        return (
            <ProductForm title="Add Product" submitCallback={this.add} isSubmitting={this.props.isSubmitting} ></ProductForm>
        )

  }
}

const mapStateToProps = (store) =>{
    isSubmitting: store.props.isSubmitting
}

const mapDispatchToProps = dispatch => {
    addProduct_ac: (data,files) => dispatchEvent(addProduct_ac(data,files))
}

export default connect(mapStateToProps, mapDispatchToProps)(AddProduct);
