import React, { Component } from "react";
import { Link } from 'react-router-dom';
import httpClient from "../../util/httpClient";
import notify from "../../util/notify";
import Loader from "../../common/loader/loader.component";
import { Icon, InlineIcon } from "@iconify/react";
import editFilled from "@iconify/icons-ant-design/edit-filled";
import closeSquareFilled from "@iconify/icons-ant-design/close-square-filled";
import "./viewProduct.component.css";
import DateUtil from './../../util/dateUtil';


const imgURL = process.env.REACT_APP_IMG_URL;

export default class ViewProduct extends Component {
  constructor() {
    super();
    this.state = {
      isLoading: false,
      products: [],
      // isDeleting: false,
    };
  }

  componentDidMount() {
    this.setState({
      isLoading: true,
    });
    httpClient
      .GET("/product", true)
      .then((response) => {
        console.log("console response >>", response);
        response.data.forEach(item => {
          item.$$isDeleting = false;
        })
        this.setState({
          products: response.data,
        });
      })
      .catch((err) => {
        notify.handleError(err);
      })
      .finally(() => {
        this.setState({
          isLoading: false,
        });
      });
  }

  edit(id) {
    this.props.history.push(`/edit_product/${id}`);
  }

  remove(item, index) {
    console.log("now remove", item._id);
    const { products } = this.state;
    products.find(products => {
      return (products._id == item._id).$$isDeleting = true;
    })
    this.setState({
      products
    })
    // eslint-disable-next-line no-restricted-globals
    let confirmation = confirm("Are you sure to remove?");
    if (confirmation) {
      httpClient
        .DELETE(`/product/${item._id}`, true)
        .then((response) => {
          notify.showInfo("Product Removed");
          const { products } = this.state;
          products.splice(index, 1);
          this.setState({
            products,
          });
        })
        .catch((err) => {
          notify.handleError(err);
        })

    }
  }

  render() {
    console.log('products >>',this.state.products);
    let deleting = this.state.$$isDeleting ?
    <Loader />
    :  <button
    className="btn-icons"
    title="delete"
    onClick={() => this.remove()}
  >
    <Icon icon={closeSquareFilled} color="red" />
  </button>

    let content = this.state.isLoading ? (
      <Loader />
    ) : (
      <>
        <h2>View Products</h2>
        <table className="table">
          <thead>
            <tr>
              <th>S.N</th>
              <th>Name</th>
              <th>Brand</th>
              <th>Category</th>
              <th>Created Date</th>
              <th>Created Time</th>
              <th>Images</th>
              <th>Actions</th>
            </tr>
          </thead>

          <tbody>
            {this.state.products.map((item, index) => (
              <tr key={item._id}>
                <td>{index + 1}</td>
                <td><Link to={`product_details/${item._id}`}>{item.name}</Link></td>
                <td>{item.brand}</td>
                <td>{item.category}</td>
                <td>{DateUtil.formateDate(item.createdAt)}</td>
                <td>{DateUtil.formateTime(item.createdAt)}</td>
                <td>
                  <img src={`${imgURL}/${item.images[0]}`} alt="product_img.jpg" width="150px" height="150px" />
                </td>
                <td>
                  <button
                    className="btn-icons"
                    title="edit"
                    onClick={() => this.edit(item._id)}
                  >
                    <Icon icon={editFilled} color="lightblue" />
                  </button>

                  {deleting}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </>
    );
    return content;
  }
}
