import React, { Component } from 'react';
import notify from '../../util/notify';
import Axios from 'axios';

const BaseURL = process.env.REACT_APP_BASE_URL;

const myAxios = Axios.create({
    baseURL: BaseURL,
    responseType: 'json'
})

const defaultForm = {
    sliderImage1: '',
    sliderImage2: '',
    sliderImage3: '',
    sliderImage4: '',

}
export class SliderForm extends Component{
    constructor(){
        super();
        this.state = {
            data: {
                ...defaultForm
            },
            fileUpload: []
        }
    }

    componentDidMount(){

    }

    handleChange=(e) =>{
        let {type, name,value, files} = e.target;

        if(type === 'file'){
            const {fileUpload} = this.state;
            fileUpload.push(...files)
            return this.setState({
                fileUpload
            })
        }
        this.setState({
            data: {
                [name]: value
            }
        })
    }
    handleSubmit=(e) => {
        e.preventDefault();
        // const formData = new FormData();
        // formData.append(this.state.data, this.state.fileUpload);       
        myAxios.post('/slider',this.state.data, this.state.fileUpload)
            .then(response => {
                notify.showSuccess('Slider Added Successfully');
            })
            .catch(err => {
                notify.handleError(err)
            })
    }

    render(){
        return(
            <div className="container">
                <h2>Slider Details :</h2>
                <form className="form-group" onSubmit={this.handleSubmit} encType="multipart/form-data">
                    <label>Slider Image 1</label>
                    <input type="file" name="sliderImage1" onChange={this.handleChange} className="form-control" />

                    <label>Slider Image 2</label>
                    <input type="file" name="sliderImage2" onChange={this.handleChange} className="form-control" />
                    <label>Slider Image 3</label>
                    <input type="file" name="sliderImage3" onChange={this.handleChange} className="form-control" />
                    <label>Slider Image 4</label>
                    <input type="file" name="sliderImage4" onChange={this.handleChange} className="form-control" />
                    <br/>
                    <button className="btn btn-primary" type="submit">submit</button>
                </form>
            </div>
        )
    }
}
