import React, { Component } from "react";
import { connect } from "react-redux";
import { fetchProduct_ac, productDetails_ac } from "../../../actions/products/productActions";
import {Link} from 'react-router-dom';
const imgURL = process.env.REACT_APP_IMG_URL;

class Footer extends Component {
  constructor(){
    super();

  }
  componentDidMount() {
    this.fetchData();
    console.log('ptops in footer>>', this.props)
  }
  fetchData(args){
    this.props.fetch();
  }

  handleClick = () => {
    this.props.products.filter(pro => pro.category === ca)
  }


  render() {
    const { products } =this.props;
    const cats = products.map(cat => cat.category);

    const c = cats.filter((categ,index) =>(
      cats.indexOf(categ) === index
    ))

    return (
      <div className="footer-component">
        <div className="container">
          <div className="row">
            <div className="col-md-3">
              <div className="footer-item">
                <div className="footer-logo">
                  <img
                    src={`${imgURL}/logo.jpg`}
                    className="img-footer-logo"
                    alt="logo"
                  />
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="footer-item">
                <h5>Categories</h5>
                <ul>
                  {c.map((ca, i) => (

                      <li key={i} onClick={this.handleClick}>{ca}</li>
                  ))}
                </ul>
              </div>
            </div>
            <div className="col-md-3">

            </div>
            <div className="col-md-3">

            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (store) => ({
  products: store.product.products,
});

const mapDispatchToProps = (dispatch) => ({
  fetch: () => dispatch(fetchProduct_ac()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Footer);
