const initialState = {
    isLoading: false,
    products: []
};

//TODO
export const productReducer = (state = initialState, action) => {
  switch (action.type) {
    case "GET_PRODUCTS":
      return {
        ...state,
      };

    default:
      return {
        ...state,
      };
  }
};
