import { ADD_PRODUCT_BASKET, GET_NUMBERS_BASKET } from "../actions/products/type"

const initialState = {
    basketNumbers: 0,
    cartCost: 0,
    myProducts: {}
}

export default(state = initialState, action) => {
    switch(action.type){

        case ADD_PRODUCT_BASKET:
            let addQuantity = {...state.myProducts[action.payload]}
            console.log('addQuantity is>>',addQuantity);
            return{
                basketNumbers: state.basketNumbers + 1
            }

        case GET_NUMBERS_BASKET:
            return{
                ...state
            }    

        default:
            return{...state}
    }
}
