import React, { Component } from "react";

export class Login extends Component {
  constructor() {
    super();
    this.state = {
      username: "",
      password: "",
      remember_me: false,
      chapter: 1
    };
    this.handleChanges = this.handleChanges.bind(this); //bind gareko
    console.log('Constructor is first');
  }

  // componentWillMount(){
  //   console.log('Before render component will mount');
  // }

  componentDidMount(){
    //self invoked block
    console.log('I am component did mount');
  }

  componentDidUpdate(prevProps,prevState){
    console.log('prev state>>',prevState);
    console.log('Once the component data is updated > props or state');
  }

  componentWillUnmount(){
    console.log('once component cycle is completed');
    //its purpose is to close all unfinished jobs
  }

  handleChanges(event) {
    // console.log('event >>',event);
    // console.log('event target >>',event.target);
    const { name, value } = event.target;
    // console.log("name >>", name);
    // console.log("value >>", value);
    this.setState({
      [name]: value,
    });
    //State change trigger
  }

  handleSubmit = (e) => {
    e.preventDefault();
    // const { chapter } = this.state;
    // chapter += 1;
    let chapter = this.state.chapter;
    let newChapter = chapter + 1;
    this.setState({
      chapter : newChapter
    })
    // console.log("Check state>>", this.state);
    //http with username and password
  };

  render() {
    console.log('render at second');
    return (
      <div>
        <h2>Login</h2>
        <p>Please login to start your session</p>
        <form className="form-group form-main">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            placeholder="Username"
            className="form-control"
            id="username"
            name="username"
            onChange={this.handleChanges}
          />
          <label htmlFor="password">Password</label>
          <input
            type="password"
            placeholder="Password"
            className="form-control"
            id="password"
            name="password"
            onChange={this.handleChanges}
          />
          <button className="btn btn-primary mt-2" onClick={this.handleSubmit}>
            Login
          </button>
        </form>
    <p>Current Chapter : {this.state.chapter}</p>
        <p>Don't have an account?</p>
        <p>
          Register <a href="/register">Here</a>
          <span className="pull_right">
            <a href="/forgot_password">forgot password?</a>
          </span>
        </p>
      </div>
    );
  }
}
